import { addons } from "@storybook/addons";
import { STORY_RENDERED } from "@storybook/core-events";
import Examples from "./fixtures/base.html";
addons.getChannel().addListener(STORY_RENDERED, (name) => {
    if (name.toLowerCase().startsWith("listbox-option")) {
        const checkedOption = document.getElementById("checked-option");
        checkedOption.checked = true;
        const selectedOption = document.getElementById("selected-option");
        selectedOption.selected = true;
        const checkedSelectedOption = document.getElementById("checked-selected-option");
        checkedSelectedOption.selected = true;
        checkedSelectedOption.checked = true;
    }
});
export default {
    title: "Listbox Option",
};
export const ListboxOption = () => Examples;
